    )

    try:
        subprocess.run(command, shell=True, check=True)
        return jsonify({'message': 'Video captured successfully'})
    except Exception as e:
        return jsonify({'error': str(e)}), 500

if __name__ == '__main__':
    app.run(debug=True)
